mapper.select:
select no, uid, title, content, regdt
  from notice_board 
 where no = #{no}

mapper.insert:
insert into notice_board(
	uid, title, content, regdt
) values (
	#{uid}, #{title}, #{content}, now()
)

mapper.update:
update notice_board 
   set uid = #{uid}, 
   	   title = #{title}, 
   	   content = #{content}
 where no = #{no}
